import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const RegisterPage = () => {
  const [form, setForm] = useState({ name: '', email: '', password: '' });
  const navigate = useNavigate();

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  // Submit user registration
  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/users/register', form);
      alert('Registered successfully! Redirecting to Login Page.');
      navigate('/login'); // normal user goes to login
    } catch (err) {
      alert(err.response?.data?.error || 'Registration failed');
    }
  };

  // Navigate to admin login
  const handleAdminClick = () => {
    navigate('/admin-login'); // go to admin login page
  };

  // Navigate to normal login page
  const handleLoginClick = () => {
    navigate('/login'); // go to user login page
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Name" value={form.name} onChange={handleChange} required /><br /><br />
        <input name="email" type="email" placeholder="Email" value={form.email} onChange={handleChange} required /><br /><br />
        <input name="password" type="password" placeholder="Password" value={form.password} onChange={handleChange} required /><br /><br />
        <button type="submit">Register</button>
      </form>

      <br />
      <button onClick={handleAdminClick}>Admin Login</button>
      <br /><br />
      <button onClick={handleLoginClick}>Already have an account? Login</button>
    </div>
  );
};

export default RegisterPage;
